select
  role_id, role_name, role_desc, status
from role


        and role_id = #{roleId}


        and role_name = #{roleName}


        and role_desc = #{roleDesc}


        and status = #{status}


limit #{pageable.offset}, #{pageable.pageSize}

select
  role_id, role_name, role_desc, status
from role


        and role_id = #{roleId}


        and role_name = #{roleName}


        and role_desc = #{roleDesc}


        and status = #{status}

select count(1)
from role


        and role_id = #{roleId}


        and role_name = #{roleName}


        and role_desc = #{roleDesc}


        and status = #{status}
